import React from "react";

const BookViewer = ({ bookId }) => {
  const embedUrl = `https://books.google.com/books?id=${bookId}&lpg=PA1&pg=PP1&output=embed`;

  return (
    <div>
      <iframe
        title="Google Books Viewer"
        width="600"
        height="400"
        src={embedUrl}
        frameBorder="0"
        allowFullScreen
      ></iframe>
    </div>
  );
};

export default BookViewer;
