import React from "react";

const CommentInput = () => {
  return (
    <input
      value={commentBody}
      onChange={(e) => setCommentBody(e.target.value)}
      placeholder="what do you think about this book?"
      className="comments-input"
    />
  );
};

export default CommentInput;
