import React, { useState } from "react";

const BookViewer = () => {
  const [isbn, setIsbn] = useState();
  console.log(isbn);
  const searchBook = () => {
    return (
      <iframe
        title="iframe-title"
        width="600"
        height="400"
        src="https://books.google.com/books?id=sSgVAAAAQAAJ&lpg=PA1&pg=PP1&output=embed"
        frameborder="0"
        allowfullscreen
      ></iframe>
    );
  };
  return (
    <div id="viewerCanvas">
      Google Books Api App
      <form>
        <input
          type="text"
          onChange={(e) => setIsbn(e.target.value)}
          placeholder="enter isbn number"
          onSubmit={searchBook}
        />
        <button type="submit">Submit</button>
      </form>
    </div>
  );
};

export default BookViewer;
