import React, { useState } from "react";
import CommentInput from "./CommentInput";
const CommentItem = ({ comment, i }) => {
  const [isReplying, setIsReplying] = useState(false);
  const [comments, setComments] = useState(comment);
  const onComment = (commentBody) => {
    const newComment = {
      body: commentBody,
      comment: [],
    };
    setComments((comments) => [...comments, newComment]);
  };
  return (
    <div className="individual-comment" key={i}>
      <span>{comment.body}</span>
      {isReplying ? (
        <button onClick={() => setIsReplying(!isReplying)}>Cancel</button>
      ) : (
        <button onClick={() => setIsReplying(!isReplying)}>Reply</button>
      )}
      {isReplying && <CommentInput onComment={onComment} />}
    </div>
  );
};

export default CommentItem;
