import React, { useState } from "react";
import CommentInput from "./CommentInput";
const CommentItem = (props) => {
  const [isReplying, setIsReplying] = useState(false);
  const [comments, setComments] = useState(props.comment.comments);
  const onComment = ({ newComment }) => {
    setComments((prev) => [newComment, ...prev]);
  };
  return (
    <div className="individual-comment">
      <span>{props.comment.body}</span>
      {isReplying ? (
        <button onClick={() => setIsReplying(!isReplying)}>Cancel</button>
      ) : (
        <button onClick={() => setIsReplying(!isReplying)}>Reply</button>
      )}
      {isReplying && <CommentInput onComment={onComment} key={i} />}
    </div>
  );
};

export default CommentItem;
