import React, { useState, useEffect } from "react";
import { useNavigate, useParams } from "react-router";
import PostForm from "./PostForm";
import axios from "axios";
import PostItem from "./PostItem";
import { getPosts } from "../../../express/controllers/postController";
const API_URL = "http://localhost:5000/api/posts";
const Dashboard = () => {
  const storedData = localStorage.getItem("user");
  const parsedData = JSON.parse(storedData);
  const { email, name, token, _id } = parsedData;
  const [posts, setPosts] = useState();
  const { bookId } = useParams();
  useEffect(() => {
    const getPosts = async (bookId, token) => {
      try {
        const config = {
          headers: {
            Authorization: `Bearer ${token}`,
          },
        };
        console.log(token);
        const response = await axios.get(`${API_URL}?bookId=${bookId}`, config);
        const posts = response.data;
        setPosts(posts);
        console.log("posts fetched from server: ", posts);
      } catch (error) {
        const message =
          (error.response &&
            error.response.data &&
            error.response.data.message) ||
          error.message ||
          error.toString();
        console.log(message);
      }
    };
    getPosts(bookId, token);
  }, [token]);
  return (
    <>
      <section>
        <h1> Welcome {name}</h1>
        <p>Dashboard</p>
      </section>
      <PostForm />

      <section className="content">
        {posts?.length > 0 ? (
          <div className="posts">
            {posts.map((post) => (
              <PostItem
                key={post._id}
                post={post}
                bookId={bookId}
                onPostDeleted={getPosts}
              />
            ))}
          </div>
        ) : (
          <h3> There are no comments yet</h3>
        )}
      </section>
    </>
  );
};

export default Dashboard;
